def generate_report():
    env = Environment(loader=FileSystemLoader('templates'))
    template = env.get_template('template.html')
    html = template.render(secada="30/23",
                           ref='referencia ',
                           field1='producto',
                           field2='producto2',
                           camara='2',
                           temp="57",
                           time="91",
                           date="31/01/2023")

    with open('html_report_jinja.html', 'w') as f:
        f.write(html)


